import React from "react";
import "./index.css";

function App() {
  const [data, setData] = React.useState(null);

  React.useEffect(() => {
    fetch("/products")
      .then((res) => res.json())
      .then((data) => setData(data.message));
  }, []);

  return (
    <div className="App">
        <p>{data ? "Loading..." : data}</p>
    </div>
  );
}

export default App;
