import React, { useState, useEffect } from "react";
import { Divider, Typography, Button, Col, Row } from "antd";
import Category from "./Category";
import Product from "./Product";
import Productform from "./Productform";
import { Layout } from 'antd';

import "antd/dist/antd.css"; // or 'antd/dist/antd.less'

const { Title } = Typography;
const { Header, Footer, Sider, Content } = Layout;
function App() {
  const [proddata, setProd] = useState([]);
  const [catdata, setCat] = useState([]);

  const AddprodOptions = {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      title: "React POST Request Example",
      description: " Some description",
      price: 10000,
      category: "technology",
    }),
  };

  const AddcatOptions = {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({ title: "React POST Request Example" }),
  };

  const Addprod = (requestOptions) => {
    fetch("/products", requestOptions);
  };

  const Addcat = (requestOptions) => {
    fetch("/category", requestOptions);
  };

  const getCat = () => {
    fetch("/category", {
      header: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then(function(response) {
        return response.json();
      })

      .then(function(myJson) {
        setCat(myJson);
      });
  };

  const getProd = () => {
    fetch("/products", {
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then(function(response) {
        return response.json();
      })

      .then(function(myJson) {
        setProd(myJson);
      });
  };

  useEffect(() => {
    getProd();
    getCat();
  },[]);

  return (

    <div className="App">
      <Layout>
      <Header>Header</Header>
      <Content></Content>
      <Footer>Footer</Footer>
    </Layout>

    </div>
  );
}

export default App;
