import React,{useState,useEffect} from 'react';
import { Divider } from 'antd';
import { Button } from 'antd';
import { Col, Row } from 'antd';
import { Typography } from 'antd';
import 'antd/dist/antd.css'; // or 'antd/dist/antd.less'
const { Title } = Typography;



function App() {

  const [data,setData]=useState([]);
  const [catdata, setCat] = useState([]);

  const getCat =() =>{
    fetch('/category'
    ,{
      header:{
        'Content-Type': 'application/json',
        'Accept': 'application/json'
      }
    }
  )
    .then(function(response){
      return response.json();
  })

  .then(function(myJson) {
    setCat(myJson)
  });
  }

  const getData=()=>{
    fetch('/products'
    ,{
      headers : { 
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    }

    )
      .then(function(response){
        return response.json();
      })

      .then(function(myJson) {
        setData(myJson)
      });

  }

  useEffect(()=>{

    getData()
    getCat()

  },[])

  return (

    <div className="App">
      <Row justify="start">
      <Col span={20}>

      <div>
      <Title style={ {color: "white"} }>Product List</Title>
      {
       data && data.length>0 && data.map((item)=>
       <div>
        <p>Product Ttitle: {item.title}</p>
        <p>Product Description: {item.description}</p>
        <p>Price: {item.price}</p>
        <p>Category: {item.category}</p>
        <Button  type="danger" >Delete Product</Button>
        <Divider plain>Text</Divider>
       </div>
       )
     }
      <Title style={ {color: "white"} }>Category List</Title>
      {catdata && catdata.length>0 && catdata.map((item)=>
      <div>
      <p>Category Ttitle: {item.title}</p>
      <Button  type="danger" >Delete Product</Button>
      </div>
      )
    }


    </div>
    </Col>
    <Col span={4}>
      <div>
      <Title style={ {color: "white"} }>Category List</Title>
      </div>
    </Col>
    </Row>
    </div>


  );

}


export default App;
