import React from "react";
import { PlusOutlined } from "@ant-design/icons";
import { Form, Input, Button, Select, InputNumber, Upload } from "antd";
const [proddata, setProd] = useState([]);
const getProd = () => {
  fetch("/products", {
    headers: {
      "Content-Type": "application/json",
      Accept: "application/json",
    },
  })
    .then(function(response) {
      return response.json();
    })

    .then(function(myJson) {
      setProd(myJson);
    });
};

const Addprod = (requestOptions) => {
  fetch("/products", requestOptions).then(() => getProd());
};

const Productform = () => {
  return (
    <>
      <Form
        labelCol={{ span: 10 }}
        wrapperCol={{ span: 20 }}
        layout="horizontal"
        style={{ backgroundColor: "white", marginTop: 15 }}
        onFinish={(values) =>{
          const jsonmsg = {"title":values.title};
          console.log(values)
        }}
      >
        <Form.Item style={{ color: "white" }} label="Title" name="Prodtitle">
          <Input />
        </Form.Item>
        <Form.Item style={{ color: "black" }} label="Description" name ="Description">
          <Input />
        </Form.Item>
        <Form.Item label="Category" style={{ color: "white" }} name ="Category">
          <Select>
            <Select.Option value="demo">Demo</Select.Option>
          </Select>
        </Form.Item>
        <Form.Item label="Price" name="Price">
          <InputNumber />
        </Form.Item>
        <Form.Item label="Upload" valuePropName="fileList" name="image">
          <Upload action="/upload.do" listType="picture-card">
            <div>
              <PlusOutlined />
              <div style={{ marginTop: 8 }}>Upload</div>
            </div>
          </Upload>
        </Form.Item>
        <Form.Item style={{ marginTop: 8 }}>
          <Button htmlType="submit" type="primary">Submit</Button>
        </Form.Item>
      </Form>
    </>
  );
};

export default Productform;
